let token = runtime.globalVars.Token;
let apiURL = runtime.globalVars.apiUrl;
let dataEmpresa = runtime.globalVars.dataEmpresaMitzi;

mainFunctionMesa(apiURL, token, dataEmpresa);

const WIDTH = 280; // Ancho de los objetos
	const HEIGHT = 280; // Alto de los objetos
	const COLUMN_SPACING = 150; // Espaciado entre columnas
	const ROW_SPACING = 120; // Espaciado entre filas
	const INITIAL_X = 300; // Posición inicial en el eje x
	const INITIAL_Y = 800; // Posición inicial en el eje y
	const MAX_OBJECTS_PER_ROW = 8; // Máximo de objetos por fila

	let dataEmpresaMitzi = JSON.parse(runtime.globalVars.dataEmpresaMitzi);
	let tipo = 1; // Tipo de consulta a la API para ventas

	const mesas = {
		normales: {
			Cuadrada2: 0,
			Cuadrada4: 3,
			Cuadrada6: 6,
			Cadrada62: 9,
			Cuadrada8: 12,
			Cuadrada82: 15,
			Redonda2: 18,
			Redonda4: 21,
			Redonda5: 24,
			Redonda6: 27,
			Redonda8: 30,
			Redonda10: 33,
			Booth1: 36,
			Booth2: 39,
			BoothClasico: 42,
			BoothEsquina: 45
		},
		activas: {
			Cuadrada2: 2,
			Cuadrada4: 5,
			Cuadrada6: 8,
			Cadrada62: 11,
			Cuadrada8: 14,
			Cuadrada82: 17,
			Redonda2: 20,
			Redonda4: 23,
			Redonda5: 26,
			Redonda6: 29,
			Redonda8: 32,
			Redonda10: 35,
			Booth1: 38,
			Booth2: 41,
			BoothClasico: 44,
			BoothEsquina: 47
		}
	};


mainFunctionToolbar();

function mainFunctionToolbar(){

	verificarPosicionesGuardadas();
	verificarVentasActivas();


	function verificarVentasActivas() {
		fetch(runtime.globalVars.apiUrl + '/web/ventas/ventas', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json'
			},
			body: JSON.stringify({
				token: runtime.globalVars.Token,
				tipo: tipo
			})
		})
		.then(response => response.json())
		.then(data => {
			console.log("Data de ventas recibida:", data);
			if (data.response) {
				actualizarMesasActivas(data.data);
			} else {
				console.error("Error al obtener ventas activas:", data.message);
			}
		})
		.catch(error => {
			console.error("Error en la solicitud de ventas activas:", error);
		});
	}

	const areaToLayerMap = {}; // Mapa para almacenar id_area_mesa a nombres de capas
	let layerCounter = 1;

	function getLayerNameByAreaId(id_area_mesa) {
		if (!areaToLayerMap[id_area_mesa]) {
			areaToLayerMap[id_area_mesa] = `areaMesa${layerCounter++}`;
		}
		return areaToLayerMap[id_area_mesa];
	}

function actualizarMesasActivas(ventas) {
    if (!ventas || ventas.length === 0) {
        console.warn('No hay ventas para procesar.');
        return;
    }

    ventas.forEach(venta => {
        const { mesa, id_area_mesa } = venta;

        if (mesa) {
            let mesaNumero = parseInt(mesa);
            let nombreCapa;

            if (id_area_mesa === null || id_area_mesa === undefined) {
                nombreCapa = 'areaMesa1'; // Asegurarse de que 'Principal' se refiere a 'areaMesa1'
            } else {
                nombreCapa = getLayerNameByAreaId(id_area_mesa);
            }

            if (!nombreCapa) {
                console.error(`No se pudo determinar el nombre de la capa para el área ${id_area_mesa}`);
                return;
            }

            let capaMesa = runtime.layout.getLayer(nombreCapa);

            if (capaMesa && capaMesa.instances) {
                let instanciaMesa = capaMesa.instances.find(inst => inst.properties && inst.properties.numeroMesa === mesaNumero);

                if (instanciaMesa) {
                    let tipoMesa = instanciaMesa.properties.tipoMesa || 'Cuadrada2';
                    if (mesas.activas && mesas.activas[tipoMesa] !== undefined) {
                        console.log(`Actualizando mesa ${mesaNumero} de tipo ${tipoMesa} con frame ${mesas.activas[tipoMesa]}`);
                        instanciaMesa.setAnimationFrame(mesas.activas[tipoMesa]);
                    } else {
                        console.error(`No se encontró frame activo para el tipo de mesa ${tipoMesa}`);
                    }
                } else {
                    console.error(`No se encontró instancia para mesa ${mesaNumero} en capa ${nombreCapa}`);
                }
            } else {
                console.error(`Capa ${nombreCapa} no accesible o sin instancias`);
            }
        } else {
            console.warn(`Datos incompletos para la venta: mesa=${mesa}, id_area_mesa=${id_area_mesa}`);
        }
    });
}



	function crearObjetoDeFamilia(nombreObjeto, id_area_mesa, x, y, ancho, alto, tipoMesa = 'AllMesas') {
		let nombreCapa = getLayerNameByAreaId(id_area_mesa);
		var objeto = runtime.objects.AllMesas;
		if (objeto) {
			console.log(`Objeto ${nombreObjeto} encontrado.`);
			var capa = runtime.layout.getLayer(nombreCapa);
			if (capa) {
				console.log(`Capa ${nombreCapa} encontrada.`);
				var instancia = objeto.createInstance(capa.index, x, y);
				if (instancia) {
					instancia.x = x;
					instancia.y = y;
					instancia.width = ancho;
					instancia.height = alto;
					instancia.tipoMesa = tipoMesa; // Añadir propiedad tipoMesa
					console.log(`Instancia de ${nombreObjeto} creada en la capa ${nombreCapa} en (${x}, ${y}) con tamaño (${ancho}, ${alto}) y tipo de mesa ${tipoMesa}.`);
					instancia.behaviors.DragDrop.isEnabled = false;
				} else {
					console.error("No se pudo crear la instancia del objeto:", nombreObjeto);
				}
			} else {
				console.error("La capa '", nombreCapa, "' no existe.");
			}
		} else {
			console.error("El objeto '", nombreObjeto, "' no existe.");
		}
	}


	function verificarPosicionesGuardadas() {
		fetch(runtime.globalVars.apiUrl + '/web/empresas/actualizaToolbar', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json'
			},
			body: JSON.stringify({
				token: runtime.globalVars.Token,
				toolbar: "check"
			})
		})
		.then(response => response.json())
		.then(data => {
			if (data.response && data.data && data.data.toolbar) {
				let toolbarData = JSON.parse(data.data.toolbar);
				// Lógica para cargar las posiciones guardadas
				cargarPosicionesGuardadas(toolbarData);
			} else {
				console.log('No se encontraron posiciones guardadas. Generando posiciones por defecto.');
				let posicionesMesas = crearMesasPorArea(dataEmpresaMitzi);
				guardarPosicionesIniciales(posicionesMesas);
			}
			inicializarSelectAreas(dataEmpresaMitzi);
		})
		.catch(error => {
			console.error('Error al verificar posiciones guardadas:', error);
		});
	}

	function crearMesasPorArea(dataEmpresa) {
		let empresa = dataEmpresa[0];
		let areasMesas = empresa.areas_mesas;

		let cantidadMesasPrincipal = empresa.mesas;
		let capaNombrePrincipal = "areaMesa1";
		let nombreObjetoPrincipal = "Mesa2C";
		let tipoMesa = nombreObjetoPrincipal; // Tipo de mesa por defecto

		let x = INITIAL_X;
		let y = INITIAL_Y;
		let posicionesMesas = [];

		for (let i = 0; i < cantidadMesasPrincipal; i++) {
			crearObjetoDeFamilia(nombreObjetoPrincipal, capaNombrePrincipal, x, y, WIDTH, HEIGHT, tipoMesa);
			posicionesMesas.push({
				numeroMesa: i + 1,
				x: x,
				y: y,
				width: WIDTH,
				height: HEIGHT,
				area: 'principal',
				tipoMesa: tipoMesa // Añadir tipoMesa
			});

			x += WIDTH + COLUMN_SPACING;
			if ((i + 1) % MAX_OBJECTS_PER_ROW === 0) {
				x = INITIAL_X;
				y += HEIGHT + ROW_SPACING;
			}
		}

		areasMesas.forEach((area, index) => {
			let cantidadMesas = area.mesas;
			let capaNombre = `areaMesa${index + 2}`;

			x = INITIAL_X;
			y = INITIAL_Y;

			for (let i = 0; i < cantidadMesas; i++) {
				crearObjetoDeFamilia(nombreObjetoPrincipal, capaNombre, x, y, WIDTH, HEIGHT, tipoMesa);
				posicionesMesas.push({
					numeroMesa: i + 1,
					x: x,
					y: y,
					width: WIDTH,
					height: HEIGHT,
					id_area_mesa: area.id_area_mesa,
					tipoMesa: tipoMesa // Añadir tipoMesa
				});

				x += WIDTH + COLUMN_SPACING;
				if ((i + 1) % MAX_OBJECTS_PER_ROW === 0) {
					x = INITIAL_X;
					y += HEIGHT + ROW_SPACING;
				}
			}
		});

		ocultarYDesactivarCapas(areasMesas.length + 1);
		return posicionesMesas;
	}


	function guardarPosicionesIniciales(posicionesMesas) {
		let toolbarDataPrincipal = {
			mesas: posicionesMesas.filter(mesa => mesa.area === 'principal')
		};

		fetch(runtime.globalVars.apiUrl + '/web/empresas/actualizaToolbar', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json'
			},
			body: JSON.stringify({
				token: runtime.globalVars.Token,
				toolbar: JSON.stringify(toolbarDataPrincipal)
			})
		})
		.then(response => response.json())
		.then(data => {
			if (data.response) {
				console.log('Posiciones iniciales del área principal guardadas exitosamente.');
			} else {
				console.error('Error al guardar posiciones iniciales del área principal:', data.message);
			}
		})
		.catch(error => {
			console.error('Error al guardar posiciones iniciales del área principal:', error);
		});

		let areasMesas = dataEmpresaMitzi[0].areas_mesas;

		areasMesas.forEach((area, index) => {
			let toolbarDataArea = {
				mesas: posicionesMesas.filter(mesa => mesa.id_area_mesa === area.id_area_mesa)
			};

			fetch(runtime.globalVars.apiUrl + '/web/areas_mesas/actualizaToolbar', {
				method: 'POST',
				headers: {
					'Content-Type': 'application/json'
				},
				body: JSON.stringify({
					token: runtime.globalVars.Token,
					id_area_mesa: area.id_area_mesa,
					toolbar: JSON.stringify(toolbarDataArea)
				})
			})
			.then(response => response.json())
			.then(data => {
				if (data.response) {
					console.log(`Posiciones iniciales del área ${area.nombre} guardadas exitosamente.`);
				} else {
					console.error(`Error al guardar posiciones iniciales del área ${area.nombre}:`, data.message);
				}
			})
			.catch(error => {
				console.error(`Error al guardar posiciones iniciales del área ${area.nombre}:`, error);
			});
		});
	}

	function cargarPosicionesGuardadas(toolbarData) {
		toolbarData.mesas.forEach(mesa => {
			let capaNombre = mesa.id_area_mesa ? `areaMesa${mesa.id_area_mesa + 1}` : "areaMesa1";
			crearObjetoDeFamilia(mesa.tipoMesa, capaNombre, mesa.x, mesa.y, mesa.width, mesa.height, mesa.tipoMesa);
		});
	}


	function ocultarYDesactivarCapas(numCapas) {
		for (let i = 2; i <= numCapas; i++) {
			let capaNombre = `areaMesa${i}`;
			let capa = runtime.layout.getLayer(capaNombre);
			if (capa) {
				capa.visible = false;
				capa.opacity = 0;
				capa.isInteractive = false; // Desactivar interactividad
			}
		}
	}

	function inicializarSelectAreas(dataEmpresa) {
		let select = document.getElementById('selectAreas');
		if (!select) return;

		let optionPrincipal = document.createElement('option');
		optionPrincipal.value = "areaMesa1";
		optionPrincipal.text = "Principal";
		select.appendChild(optionPrincipal);

		let empresa = dataEmpresa[0];
		let areasMesas = empresa.areas_mesas;

		areasMesas.forEach((area, index) => {
			let option = document.createElement('option');
			option.value = `areaMesa${index + 2}`; // Usar el nombre de la capa como valor
			option.text = area.nombre;
			select.appendChild(option);
		});

		select.addEventListener('change', (event) => {
			let selectedArea = event.target.value;
			actualizarVisibilidadCapas(selectedArea, areasMesas.length + 1);
		});
	}

	function actualizarVisibilidadCapas(areaSeleccionada, numCapas) {
		for (let i = 1; i <= numCapas; i++) {
			let capaNombre = `areaMesa${i}`;
			let capa = runtime.layout.getLayer(capaNombre);
			if (capa) {
				if (capaNombre === areaSeleccionada) {
					capa.visible = true;
					capa.opacity = 1;
					capa.isInteractive = true; // Activar interactividad
				} else {
					capa.visible = false;
					capa.opacity = 0;
					capa.isInteractive = false; // Desactivar interactividad
				}
			}
		}
	}
}

crearVenta();
function crearVenta() {
    // Obtener las variables globales de Construct 3
    let idMesero = runtime.globalVars.IdMesero;
    let idArea = runtime.globalVars.IdArea;
    let numeroMesa = runtime.globalVars.NumeroMesa;
    let numeroComensales = runtime.globalVars.NumeroComensales;
    let apiUrl = runtime.globalVars.apiUrl;
    let token = runtime.globalVars.Token;

    // Crear el cuerpo de la solicitud
	//HACER LOGICA PARA MANDAR DATOS OPCIONALES O NO
    let body = JSON.stringify({
        token: token,
        //id_mesero: idMesero,
        //id_area_mesa: idArea,
        mesa: numeroMesa,
        comensales: numeroComensales
    });

    // Realizar la solicitud POST para crear una venta
    fetch(apiUrl + '/web/ventas/crea', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: body
    })
    .then(response => response.json())
    .then(data => {
        if (data.response) {
            console.log("Venta creada exitosamente:", data.message);
            runtime.globalVars.IdVenta = data.data.id_venta; // Guardar el id de la venta en una variable global
            runtime.goToLayout("MesaAbiertaLayout"); // Navegar al layout MesaAbiertaLayout
        } else {
            console.error('Error al crear la venta:', data.message);
        }
    })
    .catch(error => {
        console.error('Error:', error);
    });
}
