actions.script:
validarPIN();

function validarPIN() {
    fetch(runtime.globalVars.apiUrl + '/web/usuarios/validaMesero', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            token: runtime.globalVars.Token,
			pin: runtime.globalVars.PinString
        })
    })
    .then(response => response.json())
    .then(data => {

		console.log(runtime.globalVars.Token);
		console.log(runtime.globalVars.PinString);

        if (data.response) {
			runtime.goToLayout("MesasLayout");
            console.log(data.data);
        } else {
            console.error('No se pudo ingresar', data.message);
        }
    })
    .catch(error => {
        console.error('Error:', error);
    });
}

validarPIN();

function validarPIN() {
    fetch(runtime.globalVars.apiUrl + '/web/usuarios/validaMesero', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            token: runtime.globalVars.Token,
            pin: runtime.globalVars.PinString
        })
    })
    .then(response => response.json())
    .then(data => {
        console.log(runtime.globalVars.Token);
        console.log(runtime.globalVars.PinString);

        if (data.response) {
            // Guardar el id_mesero en la variable global IdMesero
            runtime.globalVars.IdMesero = data.data.id_mesero;
            console.log("ID Mesero:", runtime.globalVars.IdMesero);

            // Navegar a MesasLayout
            runtime.goToLayout("MesasLayout");
            console.log(data.data);
        } else {
            console.error('No se pudo ingresar', data.message);
        }
    })
    .catch(error => {
        console.error('Error:', error);
    });
}
